css:
atom-text-editor .gutter[gutter-name="blame"],
atom-text-editor::shadow .gutter[gutter-name="blame"] {
  border-right: 1px solid #babfca;
}
atom-text-editor .blame-gutter,
atom-text-editor::shadow .blame-gutter {
  width: 100%;
}
atom-text-editor .blame-gutter .blame-gutter-inner,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner {
  padding: 0 5px 0 5px;
  box-sizing: border-box;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow-x: hidden;
  width: 100%;
  text-align: left;
}
atom-text-editor .blame-gutter .blame-gutter-inner.blame-odd,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner.blame-odd {
  background-color: rgba(255, 255, 255, 0.02);
}
atom-text-editor .blame-gutter .blame-gutter-inner.blame-even,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner.blame-even {
  background-color: rgba(0, 0, 0, 0.02);
}
atom-text-editor .blame-gutter .blame-gutter-inner:hover,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner:hover {
  padding-right: calc(12px + 2em);
}
atom-text-editor .blame-gutter .blame-gutter-inner:hover .icon,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner:hover .icon {
  display: block;
}
atom-text-editor .blame-gutter .blame-gutter-inner .blame-gutter-handle,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner .blame-gutter-handle {
  position: absolute;
  width: 4px;
  height: 100%;
  right: 0;
  top: 0;
  cursor: col-resize;
}
atom-text-editor .blame-gutter .blame-gutter-inner .icon,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner .icon {
  display: none;
  cursor: pointer;
  padding-right: 5px;
  position: absolute;
}
atom-text-editor .blame-gutter .blame-gutter-inner .icon-copy,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner .icon-copy {
  right: calc(1em + 2px);
}
atom-text-editor .blame-gutter .blame-gutter-inner .icon-copy::before,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner .icon-copy::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 1em;
  width: 1em;
  height: 1em;
  content: "\f035";
}
atom-text-editor .blame-gutter .blame-gutter-inner .icon-copy:hover,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner .icon-copy:hover {
  color: #1492ff;
}
atom-text-editor .blame-gutter .blame-gutter-inner .icon-link,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner .icon-link {
  right: 0;
}
atom-text-editor .blame-gutter .blame-gutter-inner .icon-link::before,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner .icon-link::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 1em;
  width: 1em;
  height: 1em;
  content: "\f07f";
}
atom-text-editor .blame-gutter .blame-gutter-inner .icon-link:hover,
atom-text-editor::shadow .blame-gutter .blame-gutter-inner .icon-link:hover {
  color: #1492ff;
}
.blame-tooltip {
  text-align: left;
  max-width: 500px;
  min-width: 200px;
}
.blame-tooltip .head {
  padding: 0 0 0 50px;
  margin: 0;
  height: 40px;
  position: relative;
}
.blame-tooltip .head .avatar {
  width: 40px;
  height: 40px;
  position: absolute;
  left: 0;
  top: 0;
}
.blame-tooltip .head .subject {
  height: 20px;
  line-height: 20px;
  font-size: 14px;
  font-weight: bold;
  overflow: hidden;
  text-overflow: ellipsis;
  width: 100%;
  white-space: nowrap;
}
.blame-tooltip .head .author {
  height: 20px;
  line-height: 20px;
  font-size: 10px;
  font-weight: normal;
}
.blame-tooltip .body {
  white-space: pre-wrap;
  margin-top: 10px;
  line-height: 1.4;
}
.blame-tooltip .body:empty {
  display: none;
}
